sp_rectangle_menu_item.record_script:
var max = 30;

var t = data;  // shortcut
t.items = [];

var u = gs.getUser().getID();
var tbl = new CMDBEditor().getStagedChangeTable();

// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];

t.record_watchers.push({'table':tbl,'filter':'committed=false^submitter=' + u});

var z = new GlideRecord(tbl);
z.addQuery('submitter', u);
z.addQuery('committed', false);
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();
while (z.next()) {
  var a = {};
  a.title = z.configuration_item.getDisplayValue();
  a.href = '/cmdb_editor?id=editor_home&staged_change=' + z.sys_id.toString();
  a.type = 'link';

  var gdt = new GlideDateTime();
  gdt.setDisplayValue(z.sys_updated_on.getDisplayValue());
  a.sortOrder = gdt.getNumericValue(); 

  t.items.push(a);
}

t.items.sort(function(a, b) {
  return b.sortOrder - a.sortOrder;
});
t.items = t.items.slice(0, max); // only want first 30
t.count = t.items.length;
